const fetch_auth = async (url, method, body) => {
  //check if access token available
  let accessToken = localStorage.getItem('accessToken');
  if(accessToken){
    const headers = {
      'Content-Type': 'application/x-www-form-urlencoded',
      'Accept': 'application/json',
      "Authorization": "Bearer " + localStorage.getItem("accessToken")
    };
    try{
      console.log(url);
      const response = await fetch(url, {
        method: method,
        headers: headers,
        body: body
      });
      console.log(response);
      //check if access token was not expired
      if(response.status !== 401)
        return response;
    }
    catch(error){
      console.log(error);
    }
    
  }

  console.log("Access Token is expired.");

  const refreshToken = localStorage.getItem("refreshToken");
  if(!refreshToken){
    localStorage.removeItem("accessToken");
    localStorage.removeItem("refreshToken");
    return null;
  }
  console.log(refreshToken);
  const response = await fetch(
    servers.api + "/auth/refresh", 
    {
      method: "POST",
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Accept': 'application/json',
        "Authorization": "Bearer " + refreshToken
      },
      body: //encode refreshToken
      "refreshToken="+ encodeURIComponent(refreshToken)
    }
  );
  
  if(response.status !== 200){
    console.log(await response.json());
    return null;
  }
  
  const json = await response.json();
  localStorage.setItem("accessToken", json.accessToken);
  // localStorage.setItem("refreshToken", json.refreshToken);
  return await fetch(url, {
    method: method,
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded',
      'Accept': 'application/json',
      "Authorization": "Bearer " + json.accessToken
    },
    body: body
  });
}

  

// confirmation modal
let cModal, cTitle, cBody, cNegative, cPostive, backdrop;

let toast, toast_img, toast_desc;

//on document ready
function initializeComponents(){

// confirmation modal
  cModal = document.getElementById("confirmation-modal");
  cTitle = document.getElementById("confirmation-title");
  cBody = document.getElementById("confirmation-body");
  cNegative = document.getElementById("confirmation-negative");
  cPostive = document.getElementById("confirmation-positive");
  backdrop = document.getElementById("backdrop");

  toast = document.getElementById("toast");
  toast_img = toast.querySelector("#img");
  toast_desc = toast.querySelector("#desc");
}


function closeModal() {
  backdrop.style.display = "none"
  cModal.style.display = "none"
  cModal.classList.remove("show")
}

function confirmation(title, body, postive, negative) {
  backdrop.style.display = "block"
  cModal.style.display = "block"
  cModal.classList.add("show")

  cTitle.innerHTML = title;
  cBody.innerHTML = body;
  cPostive.onclick = event=>{
    postive();
    closeModal();
  } 
  cNegative.onclick = event=>{
    if(negative)
    negative();
    closeModal();
  } 
}

function launch_toast(symbol, message) {
  
  toast_img.innerHTML = symbol;
  toast_desc.innerHTML = message;
  toast.className = "show";
  setTimeout(function () {
    toast.className = toast.className.replace("show", "");
  }, 3400);
  
}



